sys_script_include.script:
var FieldUpdater = Class.create();
FieldUpdater.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    updateField: function() {
        try {
            var recordId = this.getParameter('sysparm_record_id');
			gs.info('TEST log:' + recordId);
            var gr = new GlideRecord('incident');

            if (gr.get(recordId)) {
                gr.comments = 'Updated via confirmation dialog at ' + new GlideDateTime().getDisplayValue();
                return gr.update() ? 'success' : 'failed';
            }
            return 'failed';
        } catch (e) {
            gs.error('Field update failed: ' + e.getMessage());
            return 'failed';
        }
    },
    type: 'FieldUpdater'
});
